    fig, ax = plt.subplots(figsize=(6, 4), dpi=300)
    for lr, run in runs.items():
        # Accept both "val f1" and "val_f1"
        val_f1 = run.get("metrics", {}).get("val f1", []) or run.get("metrics", {}).get("val_f1", [])
