import os
import numpy as np

# ---------------------------------------------------------------------------
# locate and load the saved experiment data
# ---------------------------------------------------------------------------
working_dir = os.path.join(os.getcwd(), "working")
npy_path = os.path.join(working_dir, "experiment_data.npy")

if not os.path.isfile(npy_path):
    raise FileNotFoundError(f"Could not find experiment data at: {npy_path}")

experiment_data = np.load(npy_path, allow_pickle=True).item()


# ---------------------------------------------------------------------------
# helper to pick best value (loss → lower is better; others → higher is better)
# ---------------------------------------------------------------------------
def best_value(values, higher_is_better=True):
    return max(values) if higher_is_better else min(values)


# ---------------------------------------------------------------------------
# iterate over datasets and print best metrics
# ---------------------------------------------------------------------------
for dataset_name, data in experiment_data.items():
    print(dataset_name)  # dataset header

    # ----- losses -----------------------------------------------------------
    train_losses = [loss for _, loss in data.get("losses", {}).get("train", [])]
    val_losses = [loss for _, loss in data.get("losses", {}).get("val", [])]

    if train_losses:
        print(
            f"best training loss: {best_value(train_losses, higher_is_better=False):.4f}"
        )
    if val_losses:
        print(
            f"best validation loss: {best_value(val_losses,   higher_is_better=False):.4f}"
        )

    # ----- validation metrics (CWA / SWA / EWA) -----------------------------
    val_metric_entries = data.get("metrics", {}).get("val", [])

    # gather metric values per name
    metric_values = {}
    for _, metric_dict in val_metric_entries:
        for m_name, m_val in metric_dict.items():
            metric_values.setdefault(m_name, []).append(m_val)

    # print best value for each metric
    for m_name, vals in metric_values.items():
        print(
            f"best validation {m_name}: {best_value(vals, higher_is_better=True):.4f}"
        )

